// SPDX-License-Identifier: MIT

pragma solidity ^0.7.6;

contract Game {

	// bet per round
	uint public BET = 10;
    
    // possible choices for a player
    enum Choices { none, rock, paper, scissors }
    // possible results of the game
    enum Results { none, draw, player1, player2 }

	// timestamp from first reveal
	uint private revealTime = 0;

    // player addresses
    address payable public player1;
    address payable public player2;

    // player secrets choices
    bytes32 private player1SecretChoice;
    bytes32 private player2SecretChoice;
    
    // player choices
    Choices private player1Choice;
    Choices private player2Choice;

    //////////////////////////////////////////////////////////////////////////////////
    
    modifier isRegistered() {
        require (msg.sender == player1 || msg.sender == player2);
        _;
    }
    modifier isNotRegistered() {
        require (msg.sender != player1 && msg.sender != player2);
        _;
    }
    modifier commitFinished() {
        require(player1SecretChoice != 0x0 && player2SecretChoice != 0x0);
        _;
    }
    modifier revealFinished() {
        require(
        	(player1Choice != Choices.none && player2Choice != Choices.none) ||
        	(revealTime != 0 && block.timestamp > revealTime + 10 minutes)
        );
        _;
    }
    
    //////////////////////////////////////////////////////////////////////////////////
    
    // register a new player, returns true on success
    function register() public isNotRegistered returns(bool) {
        if (player1 == address(0x0)) {
            player1 = msg.sender;
            //initialBet = msg.value;
            return true;
        } else if (player2 == address(0x0)) {
            player2 = msg.sender;
            return true;
        }
        return false;
    }

    // play the game by commiting a secret choice, returns true on success
    function commit(bytes32 secretChoice) public isRegistered returns(bool) {
         if (msg.sender == player1 && player1SecretChoice == 0x0) {
            player1SecretChoice = secretChoice;
        } else if (msg.sender == player2 && player2SecretChoice == 0x0) {
            player2SecretChoice = secretChoice;
        } else {
            return false;
        }
        return true;
    }
    
    function reveal(Choices choice, string memory secret) public isRegistered commitFinished returns (Choices) {
        bytes32 calculatedChoice = sha256(abi.encodePacked(choice, secret));

        // If hashes match, clear move is saved
        if (msg.sender == player1 && player1SecretChoice == calculatedChoice) {
            player1Choice = choice;
        } else if (msg.sender == player2 && player2SecretChoice == calculatedChoice) {
            player2Choice = choice;
        } else {
            return Choices.none;
        }
        
        // save timestamp after first reveal
        if (revealTime == 0) {
            revealTime = block.timestamp;
        }
        return choice;
    }

    // TODO: time based payout

    function settle() public revealFinished returns(Results) {
    	if (player1Choice == player2Choice) {
            player1.transfer(BET * 950 / 1000); // CALL NUTZEN !!
            player2.transfer(BET * 950 / 1000);
            return Results.draw;
        } else if ((player1Choice == Choices.rock && player2Choice == Choices.scissors) ||
        			//(player1Choice != Choices.None && player2Choice == Choices.None) ||
                   (player1Choice == Choices.paper && player2Choice == Choices.rock) ||
                   (player1Choice == Choices.scissors && player2Choice == Choices.paper)) {
            player1.transfer(BET * 950 / 1000);
            return Results.player1;
        } else {
            player2.transfer(BET * 950 / 1000);
            return Results.player2;
        }
    }
}
